
-- T?o b?ng cho Categories
CREATE TABLE Categories(
    Category_ID NUMBER GENERATED ALWAYS AS IDENTITY NOT NULL PRIMARY KEY,
    Category_NAME VARCHAR2(50)
);
-- T?o b?ng cho Sub_categories
CREATE TABLE Sub_categories(
    Sub_category_ID NUMBER GENERATED ALWAYS AS IDENTITY NOT NULL PRIMARY KEY,
    Category_ID NUMBER NOT NULL,
    Sub_category_NAME VARCHAR2(265)
);

-- T?o b?ng cho Advertisement
CREATE TABLE Advertisement(
    Advertisement_ID NUMBER GENERATED ALWAYS AS IDENTITY  NOT NULL PRIMARY KEY,
    Description_Advertisement NCLOB,
    URL_LINK NCLOB,
    URL_Image NCLOB,
    CountClickedd NUMBER,
    Date_Advertisement DATE
);
-- T?o b?ng co User
CREATE TABLE USERS_P(
    User_ID NUMBER GENERATED ALWAYS AS IDENTITY NOT NULL PRIMARY KEY,
    First_NAME VARCHAR2(50),
    Last_NAME VARCHAR2(50),
    Full_NAEM VARCHAR2(75),
    Email VARCHAR2(100),
    Password_User VARCHAR2(25),
    Address VARCHAR2(75),
    Phone_Number NUMBER,
    Date_Register DATE,
    Type_user VARCHAR2(20),
    BirthDay DATE,
    Status NUMBER
);

-- T?o b?ng POST
CREATE TABLE POST(
    Post_ID NUMBER GENERATED ALWAYS AS IDENTITY NOT NULL PRIMARY KEY,
    Title VARCHAR2(365),
    Content_Post NCLOB,
    Brief NCLOB,
    Date_Public DATE,
    UrlImage NCLOB,
    User_ID NUMBER,
    Tags VARCHAR2(50),
    Category_ID NUMBER,
    View_Post NUMBER,
    Status NUMBER
);
-- T?o b?ng Comments
CREATE TABLE Comments(
    Comment_ID NUMBER GENERATED ALWAYS AS IDENTITY NOT NULL PRIMARY KEY,
    Email VARCHAR2(75),
    User_Name VARCHAR2(50),
    Description NCLOB,
    Date_Comment DATE
);

-- T?o b?ng Sub_Comment
CREATE TABLE Sub_Comment(
    Sub_Comment_ID NUMBER GENERATED ALWAYS AS IDENTITY NOT NULL PRIMARY KEY,
    Comment_ID NUMBER,
    Email VARCHAR2(75),
    User_Name VARCHAR2(50),
    Description NCLOB,
    Date_Comment DATE
);

-----------------------------------------------------------------------------------------------
------Các ràng bu?c cho các b?ng ?ã ??------
-- Các ràng bu?c cho b?ng 'Sub_categories'
ALTER TABLE Sub_categories
ADD CONSTRAINT pk_categories
FOREIGN KEY (Category_ID) REFERENCES Categories(Category_ID) ON DELETE CASCADE;

-- Các ràng bu?c cho b?ng 'POST'
ALTER TABLE POST
ADD CONSTRAINT pk_Users_P FOREIGN KEY (User_ID) REFERENCES USERS_P(User_ID) ON DELETE CASCADE;
ALTER TABLE POST
ADD CONSTRAINT pk_POST_Categories FOREIGN KEY (Category_ID) REFERENCES Categories(Category_ID) ON DELETE CASCADE;

-- Các ràng bu?c cho bàng 'Sub_Comment'
ALTER TABLE Sub_Comment
ADD CONSTRAINT pk_Comments 
FOREIGN KEY(Comment_ID) REFERENCES Comments(Comment_ID) ON DELETE CASCADE;
