INSERT INTO "BTLORACLE"."POST" (TITLE, CONTENT_POST, BRIEF, DATE_PUBLIC, URLIMAGE, USER_ID, TAGS, CATEGORY_ID, VIEW_POST, STATUS) VALUES ('Phim ''Huy?n s? vua ?inh'' kém v? k?ch b?n l?n t?o hình', 'Tác ph?m c?a ??o di?n Anthony Võ là m?t trong nh?ng phim Vi?t hi?m hoi ra r?p d?p cu?i n?m. Theo ?u?i th? lo?i dã s? (có nhi?u chi ti?t h? c?u t? l?ch s?), phim tái hi?n chân dung ?inh Tiên Hoàng, t? thu? còn là thanh niên ??n lúc d?p lo?n 12 s? quân, lên ngôi hoàng ??, ??t tên n??c là ??i C? Vi?t.

