INSERT INTO "BTLORACLE"."POST" (TITLE, CONTENT_POST, BRIEF, DATE_PUBLIC, URLIMAGE, USER_ID, TAGS, CATEGORY_ID, VIEW_POST, STATUS) VALUES ('Chi hàng tr?m tri?u ??ng ch?i xe ??p', 'Khi b? 10 tri?u ??ng mua chi?c xe ??p ??u tiên cách ?ây ch?c n?m, ông Thu?n không ng? có ngày nhà mình có h?n 30 chi?c xe, tr? giá vài tr?m tri?u ??ng.

