----create trigger 
CREATE TABLE audits (
      audit_id         NUMBER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY,
      table_name       VARCHAR2(255),
      transaction_name VARCHAR2(10),
      by_user          VARCHAR2(30),
      transaction_date DATE
);
    ALTER TABLE audits
    ADD detail NCLOB;
CREATE OR REPLACE TRIGGER NAME_CATEGORIES_TRG
    AFTER 
    UPDATE OR DELETE
    ON categories
    FOR EACH ROW
DECLARE
    L_CARTEGORY_NAME VARCHAR2(265);
    l_note varchar2(1000);
BEGIN
    L_CARTEGORY_NAME:= CASE
    WHEN UPDATING THEN 'UPDATE'
    WHEN DELETING THEN 'DELETE'
    END;
    INSERT INTO audits (table_name, transaction_name, by_user, transaction_date)
    VALUES('categories', L_CARTEGORY_NAME, USER, SYSDATE);
END;

    UPDATE CATEGORIES SET CATEGORY_NAME ='Tâm S?' WHERE category_id = 24;
End;
    DELETE categories WHERE CATEGORY_NAME = 'Tâm s?' and category_id = 24;
Select *from audits;
--------------------------------------
CREATE OR REPLACE TRIGGER POST_AUDITS_TRG
    AFTER
    UPDATE OR DELETE
    ON POST
    FOR EACH ROW
DECLARE
    l_transaction_name VARCHAR2(265);
    l_details VARCHAR2(255);
BEGIN
    l_transaction_name := CASE
    WHEN UPDATING THEN 'UPDATE'
    WHEN DELETING THEN 'DELETE'
    END;
if updating then
