CREATE OR REPLACE TRIGGER CHAGECOUNT_CLICK_ADVERTISEMENT_TRG
    AFTER 
    UPDATE OR DELETE
    ON ADVERTISEMENT
    FOR EACH ROW
DECLARE
    L_transaction_name VARCHAR2(265);
BEGIN
    L_transaction_name:= CASE
    WHEN UPDATING THEN 'UPDATE'
    WHEN DELETING THEN 'DELETE'
    END;
    INSERT INTO audits (table_name, transaction_name, by_user, transaction_date)
    VALUES('ADVERTISEMENT', L_transaction_name, USER, SYSDATE);
END;

    UPDATE ADVERTISEMENT SET countclickedd =15 WHERE ADVERTISEMENT_ID = 1;
End;
    DELETE ADVERTISEMENT WHERE countclickedd = 15 and ADVERTISEMENT_ID = 1;
