---CREATE PRODUCER
--1.vi?t stored producer, Nh?p id bài vi?t vá xu?t ra thông tin bài post ?ó
Create OR REPLACE PROCEDURE SelectPOST(in_post_id number)
    is
    r_post POST%ROWTYPE;
BEGIN
     SELECT *
     INTO r_post
     from post
     where post_id = in_post_id;
     DBMS_OUTPUT.PUT_LINE(r_post.TITLE||''||r_post.CONTENT_POST||''||
     r_post.BRIEF||''||r_post.DATE_PUBLIC||''||r_post.URLIMAGE||''||
     r_post.USER_ID||''||r_post.TAGS||''||r_post.CATEGORY_ID||''||
     r_post.VIEW_POST||''||r_post.STATUS||'.');
     EXCEPTION
     WHEN OTHERS THEN
     DBMS_OUTPUT.PUT_LINE(SQLERRM);
 END;
EXECUTE SelectPOST;
--2.vi?t stored producer, Nh?p id và nh?p categories và xu?t ra thông tin c?a categories
Create OR REPLACE PROCEDURE SelectCategories(in_category_id number,
in_CATEGORY_NAME VARCHAR2)
is
    r_category Categories%ROWTYPE;
BEGIN
 SELECT *
 INTO r_category
 from Categories
 where CATEGORY_ID = in_category_id;
    DBMS_OUTPUT.put_line(r_category.CATEGORY_ID||' '||r_category.CATEGORY_NAME||' ');
 EXCEPTION
 WHEN OTHERS THEN
 DBMS_OUTPUT.PUT_LINE(SQLERRM);
 END;
EXECUTE SelectCategories;
-------------------------------------------------------------------------------
--function and cursor
create or replace function FindPost(TITLE_in IN Varchar2)
    RETURN number
is
    total number;
CURSOR c1
IS
    Select Post_ID
    from post
    where title = TITLE_in;
BEGIN
    open c1;
    fetch c1 into total;
    if c1%notfound then
        total:=404;
        end if;
        CLOSE c1;
    RETURN TOTAL;
EXCEPTION
WHEN OTHERS THEN
   raise_application_error(-20001,'An error was encountered - '||SQLCODE||' -ERROR- '||SQLERRM);
END;


